/*2) 
●Crear un  método que nos retorne ese sonido 2 veces
Cuando esté listo, verificar en consola*/
function sonidoX2(array) {
  const sonidos = [];

  for (let i = 0; i < array.length; i++) {
    const sonido = array[i].sonido + ' ' + array[i].sonido;
    sonidos.push(sonido);
  } return sonidos
}

