// 3) Deberemos crear una función que se llame aumentarEdad que aumente la edad de
// cada animal en 1.
function aumentarEdad(array) {
  const edades = []
  for (let i = 0; i < array.length; i++) {
    array[i].edad = array[i].edad + 1
    edades.push(array[i].edad)
  } return edades
}
