// 5) Ahora crearemos una función que va a generar un identificador (ID) en cada objeto,
// que será secuencial y que empezará en 1.*/
function generarId(array) {
  let contador = 1;
  for (let i = 0; i < array.length; i++) {
    array[i].id = contador;
    contador++;
  }
}

