//3)Obtener un nuevo array de cuentas cuyas edades sean menores o igual a 30.

function masJoven(arrayCuentas) {
    let cuentaRetorno = {}
    for (let i = 0; i < arrayCuentas.length; i++) {
        const cuenta = arrayCuentas[i]
        if (cuentaRetorno.edadTitular == undefined) {
            cuentaRetorno = cuenta
        }
        if (cuenta.edadTitular < cuentaRetorno.edadTitular) {
            cuentaRetorno = cuenta
        }
    }
    return cuentaRetorno
}

