//   Escribir una función que reciba una array y solo imprima los valores que
// se repiten. Por ejemplo, dada la siguiente array e índice, la función
// imprimirá '6,23,33,100'. let array =
// [3,6,67,6,23,11,100,8,93,0,17,24,7,1,33,45,28,33,23,12,99,100];
let array1 = [3, 6, 67, 6, 23, 11, 100, 8, 93, 0, 17, 24, 7, 1, 33, 45, 28, 33, 23, 12, 99, 100];
function imprimirValoresRepetidos(arr) {
    const elementosRepetidos = [];
    for (let i = 0; i < arr.length; i++) {
        const elemento = arr[i];
        if (arr.indexOf(elemento) !== i && elementosRepetidos.indexOf(elemento) === -1) {
            elementosRepetidos.push(elemento);
        }
    }
    console.log(elementosRepetidos.join(","));
}


imprimirValoresRepetidos(array1); // Output: 6,23,33,100
