// 3) Escribir una función que, dado un número de mes, devuelva la cantidad
// de días de ese mes —suponiendo que no es un año bisiesto—.
function cantidadDiasMes(mes) {
    switch (mes) {
        case 2:
            return 'Cantidad de dias:' + 28;
        case 4:
        case 6:
        case 9:
        case 11:
            return 'Cantidad de dias:' + 30;
        default:
            return 'Cantidad de dias:' + 31;
    }
}
//console.log(cantidadDiasMes( 2));
function diasEnMes(mes) {
    if (mes === 2) {
        return 'Cantidad de dias:' + 28;
    } else if (mes === 4 || mes === 6 || mes === 9 || mes === 11) {
        return 'Cantidad de dias:' + 30;
    } else {
        return 'Cantidad de dias:' + 31;
    }
}
