// Escribir una función de JavaScript que invierta un número. Por ejemplo,
// si x = 32443, la salida debería ser 34423.
function invertirNumero(numero) {
    let numeroInvertido = 0;
    while (numero > 0) {
        let digito = numero % 10;
        numeroInvertido = (numeroInvertido * 10) + digito;
        numero = Math.floor(numero / 10);
    }
    return numeroInvertido;
}
//console.log(invertirNumero(1234))

function invertirNumero(numero) {
    let numeroInvertido = 0;
    for (let i = numero; i > 0; i = Math.floor(i / 10)) {
        let digito = i % 10;
        numeroInvertido = (numeroInvertido * 10) + digito;
    }
    return numeroInvertido;
}
//console.log(invertirNumero(4321))

//   Escribir una función que reciba una array y solo imprima los valores que
// se repiten. Por ejemplo, dada la siguiente array e índice, la función
// imprimirá '6,23,33,100'. 


function imprimirValoresRepetidos(array) {
    const elementosRepetidos = [];
    for (let i = 0; i < array.length; i++) {
        const elemento = array[i];
        if (array.indexOf(elemento) !== i && elementosRepetidos.indexOf(elemento)=== -1) {
            elementosRepetidos.push(elemento);
        }
    }console.log(elementosRepetidos.join(","));
    
}


imprimirValoresRepetidos(array); // Output: 6,23,33,100
