function totalSemana(matriz, semana) {
    let arraysemana=matriz[semana -1]
    let suma = 0;
    for (let i = 0; i <arraysemana.length; i++) {  
        suma += arraysemana[i]                   //AQUI SUMA FILAS
    }
    return suma;
}
console.log(totalSemana(gastos, 2))

function totalDia(matriz, dia) {
    let suma = 0;
    for (let i = 0; i < matriz.length; i++) {  
    suma += matriz[i][dia -1]                 //AQUI SUMA COLUMNAS
    }
    return suma
}
//console.log(totalDia(gastos, 1))

function sumaTotal(matriz) {
    let contt = 0;
    for (i = 0; i < matriz.length; i++) {
        for (j = 0; j < matriz[i].length; j++) {
            contt += matriz[i][j]                //AQUI SUMA TODO EL ARRAY 
        }
    }
return contt
}
