/**
 * Suma los valores que sean mayores
 * o iguales a 10, pero menor a 1000.
 */
const sumarvalores = matriz =>{
    let valor=0
    for (let i = 0; i < matriz.length; i++) {
        for (let j = 0; j <matriz[i].length;j++) {
            const element = matriz[i][j]
            if (element >= 10 && element < 1000) {
                console.log(`Sumando ${element}`);
            valor += element
            
            }
        }
        
        
    }
    return `La suma de los valores mayores o iguales a 10 y menores a 1000 es: ${valor}`

}
console.log(sumarvalores(numeros))