//BUSCA LA EDAD PASADA POR PARAMETRO EN ESTE CASO MENOR A 18  
function mayorMenor(arrayEdades, edad) {
    let edadesMenor = []
    let edadesMayor = []
    for (let i = 0; i < arrayEdades.length; i++)
        if (arrayEdades[i] < edad) {
            edadesMenor.push(arrayEdades[i])// push modifica mi Array agregando elementos 
            //devuelve la nueva cantidad de elementos de mi Array

        } else {
            edadesMayor.push(arrayEdades[i])
        }
    return {
        edadesMenor: edadesMenor,
        edadesMayor: edadesMayor
    }
}
