let deportista = {
    nombre: "Juan",
    energia: 100,
    experiencia: 10,
    entrenarHoras: function (cantidadHoras) {
        this.energia -= cantidadHoras * 5;
        this.experiencia += cantidadHoras * 2
    }
}
console.log(`Antes de entrenar: Energía: ${deportista.energia}, Experiencia: ${deportista.experiencia}`);

deportista.entrenarHoras(3);

console.log(`Después de entrenar: Energía: ${deportista.energia}, Experiencia: ${deportista.experiencia}`);


// nombre: Es una propiedad que contiene el nombre del deportista, en este caso "Juan".
// energia: Es una propiedad que indica la energía que tiene el deportista, en este caso tiene un valor inicial de 100.
// experiencia: Es una propiedad que indica la experiencia que tiene el deportista, en este caso tiene un valor inicial de 10.
// entrenarHoras: Es un método que simula el entrenamiento del deportista. El método recibe como parámetro la cantidad de horas que se va a entrenar (cantidadHoras), y realiza los siguientes cálculos:
// Resta a la propiedad energia la cantidad de horas multiplicada por 5, simulando el desgaste físico del entrenamiento.
// Añade a la propiedad experiencia la cantidad de horas multiplicada por 2, simulando el aprendizaje y mejora del deportista.
