function mayorMenor(arrayEdades) {
    let edadesMenor = []
    let edadesMayor = []
    for (let i = 0; i < arrayEdades.length; i++)
        if (arrayEdades[i] < 18) {
            edadesMenor.push(arrayEdades[i])

        } else {
            edadesMayor.push(arrayEdades[i])
        }
    return {
        edadesMenor: edadesMenor,
        edadesMayor: edadesMayor
    }
}
