// Crear una función que imprima la sumatoria de todos los números entre el
// 0 y el 100.
function sumatoria() {
    let suma = 0;
    for (let i = 0; i <= 100; i++) {
        suma = suma + i;
    }
    return `La sumatoria de los números entre 0 y 100 es ${suma}`
}
console.log(sumatoria())
console.log('********************************************')

// 4. Crear una función que reciba un string y luego imprimir la cantidad de
// vocales que se encuentran en dicha frase.
function contarVocales(frase) {
    let cantidadVocales = 0;
    for (let i = 0; i < frase.length; i++) {
        let letra = frase[i].toLowerCase();
        if (letra === 'a' || letra === 'e' || letra === 'i' || letra === 'o' || letra === 'u') {
            cantidadVocales++;
        }
    }
    console.log('La cantidad de vocales en la frase es: ' + cantidadVocales);

}
contarVocales(' HOla como Estan? yo supEr bien ')
console.log('********************************************')

// 5. Realizar una función que, dada una lista, devuelva una nueva lista cuyo
// contenido sea igual a la original pero invertida.
const listaOriginal = ['hola', 'mundo', 'javascript', 'programación'];
function invertirLista(lista) {
    let listaInvertida = [];
    for (let i = lista.length - 1; i >= 0; i--) {
        listaInvertida.push(lista[i]);
    }
    return listaInvertida;
}
console.log(invertirLista(listaOriginal))
console.log('********************************************')

// 6. Realizar una función que escriba una pirámide del 1 al 30 de la siguiente

function generarPiramide() {
    for (let i = 1; i <= 30; i++) {
        let linea = '';
        for (let j = 1; j <= i; j++) {
            linea = linea + i;
        }
        console.log(linea);
    }
}
generarPiramide();

