function adivinarNumero(num) {
    const numeroSecreto = Math.floor(Math.random() * 10) + 1
    if (num === numeroSecreto) {
        return "¡Felicitaciones! Adivinaste el número secreto."
    } else {
        return `Lo siento, el número que ingresaste (${num}) no es el número secreto. El número secreto era ${numeroSecreto}. ¡Inténtalo de nuevo!`
    }
}
console.log(adivinarNumero(9)); // "Lo siento,el número que ingresaste () no es el número secreto. El número secreto era 7. ¡Inténtalo de nuevo!"
console.log(adivinarNumero(1)); // "¡Felicitaciones! Adivinaste el número secreto."

function abrirParacaidas(velocidad, altura) {
    if (velocidad < 1000 && altura >= 2000 && altura < 3000) {
        return "Abre el paracaídas"
    } else {
        return "No abres el paracaídas"
    }
}
console.log(abrirParacaidas(800, 2500)); // "Abre el paracaídas"
console.log(abrirParacaidas(1200, 2800)); // "No abres el paracaídas"

