//La función tomará como entrada los parámetros de la selección del cliente y, a partir de ellos, calculará el total a pagar.

function calcularPrecio(sandwich, pan, queso, tomate, lechuga, cebolla, mayonesa, mostaza) {
    let precioBase
    let precioPan
    let precioAdicionales = 0
    switch (sandwich) {
        case "pollo":
            precioBase = 150
            break;
        case "carne":
            precioBase = 200
            break;
        case "veggie":
            precioBase = 100
            break
        default:
            precioBase = 0
    }
    switch (pan) {
        case "blanco":
            precioPan = 50
            break;
        case "negro":
            precioPan = 60
            break;
        case "s/gluten":
            precioPan = 75
            break;
        default:
            precioPan = 0
    }
    if (queso) {
        precioAdicionales += 20
    }
    if (tomate) {
        precioAdicionales += 15
    }
    if (lechuga) {
        precioAdicionales += 10
    }
    if (cebolla) {
        precioAdicionales += 15
    }
    if (mayonesa) {
        precioAdicionales += 5
    }
    if (mostaza) {
        precioAdicionales += 5
    }
    let totalAPagar = precioBase + precioPan + precioAdicionales

    return totalAPagar
}
