let matriz = [
    [14, 11, 15, 28], // indices --> 0
    [24, 34, 22, 47], // indices --> 1
    [33, 30, 21, 50] // indices --> 2
];

/* 
1 - Desarrollar una funcion que reciba una matriz cuadrada por parametro
* debe retornar un array con los elementos pares
*/
function obtenerPares(matriz) {
    let pares = [];

    for (let i = 0; i < matriz.length; i++) {
        for (let j = 0; j < matriz[i].length; j++) {
            if (matriz[i][j] % 2 === 0) {
                pares.push(matriz[i][j]);
            }
        }
    }
    return pares;
}
console.log(obtenerPares(matriz));

/* 
2 - Desarrollar una funcion que reciba una matriz cuadrada por parametro
* debe retornar un array con los elementos impares
*/
function obtenerImpares(matriz) {
    let impares = [];

    for (let i = 0; i < matriz.length; i++) {
        for (let j = 0; j < matriz[i].length; j++) {
            if (matriz[i][j] % 2 === 1) {
                impares.push(matriz[i][j]);
            }
        }
    }
    return impares;
}
console.log(obtenerImpares(matriz));

/* 
3 - Desarrollar una funcion que reciba una matriz cuadrada por parametro
* debe retornar un array con los elementos mayor a 20
*/
function obtenerMayor(matriz) {
    let mayor = [];

    for (let i = 0; i < matriz.length; i++) {
        for (let j = 0; j < matriz[i].length; j++) {
            if (matriz[i][j] > 20) {
                mayor.push(matriz[i][j]);
            }
        }
    }
    return mayor;
}
console.log(obtenerMayor(matriz))
/* 
4 - Desarrollar una funcion que reciba una matriz cuadrada por parametro
* debe retornar un array con los elementos de la primer diagonal
*/
function obtenerDiagonalPrincipal(matriz) {
    const diagonal = [];

    for (let i = 0; i < matriz.length; i++) {
        diagonal.push(matriz[i][i]);
    }

    return diagonal;
}
console.log(obtenerDiagonalPrincipal(matriz));





