function reemplazoFastFast(texto, buscar, reemplazar) {
    let textoReemplazado = texto.replace(new RegExp(buscar, 'g'), reemplazar);
    return textoReemplazado
  }
  let textoSinGuiones = reemplazoFastFast('Este texto es mala onda','mala','buena');
console.log(textoSinGuiones) // 'Este texto es buena onda'

function menciona(texto, palabra) {
    return texto.includes(palabra)
  }
 
  function menciona(texto, palabra) {
    return texto.indexOf(palabra) !== -1
  }
  console.log(menciona('Existen muchos lenguajes de programación y JavaScript es uno de ellos','programación')) // true