// HAGAMOS UN CALCULADORA 

function suma(a, b) {
    return a + b
}

function resta(a, b) {
    return a - b
}

function multiplicacion(a, b) {
    return a * b
}
function division(a, b) {
    if (b === 0) {
        return "Error: división por cero";
    }
    return a / b
}
function cuadradoDeUnNumero(num) {
    return multiplicacion(num, num)
}
function cuboDeUnNumero(num) {
    return multiplicacion(multiplicacion(num, num), num)
}
function promedioDeTresNumeros(num1, num2, num3) {
    let sumar = suma(suma(num1, num2), num3)
    return division(sumar, 3)
}
function calcularPorcentaje(total, porcentaje) {
    let resultado = multiplicacion(total, porcentaje)
    resultado = division(resultado, 100)
    return resultado
}
function GeneradorDePorcentaje(num1, num2) {
    let resultado = division(num1, num2)
    resultado = multiplicacion(resultado, 100)
    return resultado
}
