function anterior(numero) {
    return numero - 1;
}
console.log(anterior(10))

function triple(numero) {
    return numero * 3;
}
console.log(triple(5))

function anteriorDelTriple(numero) {
    let tripleNumero = triple(numero);
    let resultado = anterior(tripleNumero);
    return resultado;
}
console.log(anteriorDelTriple(2))


