//CONDICIONAL if pregunta una condicion se ejecutara siempre que ambas condiciones se cumplan
//QUE IN if NO SIEMPRE NECESITA DE UN else O UN else if 
//  QUE PUEDE TENER MUCHOS else if SI ASI LO QISIERA 
//QUE SIEMPRE UN else ,DEBERA EXISTIR SOLO UNO DE ELLOS    
let clima = 'soleado'
let dia = 'lunes'
if (clima == 'soleado' && dia == 'domingo') {
    console.log('lindo dia para pasear')
} else if (clima == 'soleado' && dia == 'lunes') {
    console.log('podria salir igual');
} else {
    console.log('mejor me quedo en casa practicand js');
}

let edad = 16   // AQUI CAMBIAMOS EL VALOR PARA IR SENTENCIANDO LAS CONSULTAS 
let acceso = ''
if (edad < 16) {
    acceso = 'prohibido'
} else if (edad >= 16 && edad <= 18) {
    acceso = 'permitido solo acompañado de un mayor'
} else {
    acceso = 'permitido'
}
console.log(acceso)
