//EJEMPLO CON FUNCIONES 
function puedePasar(nombre) {
    if (nombre == "Cosme Fulanito") {
        return false
    } else {
        return true
    }
}
console.log(puedePasar("Cosme Fulanito")); // false
console.log(puedePasar("Juan Pérez")); // true