//ESTRUCTURA PARAMETIZADA DE UN FUNCION FOR 
function sumatoria(a,b){
  let suma = 0
  for (let i = a;i <= b;i ++){
    suma += i
}
return suma
}
//console.log(sumatoria(1,5))
// imprime por pantalla el valor 2, que se obtiene de contar 1 y 3. Los valores 0, 2 y 4 no los contabiliza por ser números pares.
function noParesDeContarImparesHasta(x) {
  let contador = 0
  for (let i = 0; i <= x; i++) {
    if (i % 2 !== 0) {
      contador++
    }
  }
  return contador
}
