//EJEMPLO switch
let semaforo = 'rojo'
switch (semaforo) {
    case 'verde':
        console.log('puede cruzar')
        break
    case 'amarillo':
        console.log('precaucion')
        break
    case 'rojo':
        console.log('no cruzar,hay que esperar')
        break
    default:
        console.log('no funcion el semaforo ')
}
//MAS EJEMPLOS DE switch
