 function tengoClases(dia) {
    switch (dia) {
        case "lunes":
            console.log("Tenés clases");
            break;
        case "miércoles":
            console.log("Tenés clases");
            break;
        case "viernes":
            console.log("Tenés clases");
            break;
        case "jueves":
            console.log("No tenés clases");
            break;
        default:
            console.log("Ese día no es válido");
    }
}

