//ESTA FUNCION ME MUESTRAS LA CANTIDAD
// DE PARES QUE PASO POR PARAMETRO

function noParesDeContarPares(numero) {
    //ESCRIBE AQUI TU CODIGO
    let canInpares = 0
    for (let i = 0; i <= numero; i++) {
        if (i % 2 !== 0) { // i es impar??
            //console.log(i); // CONSULTA PARA SABER QUE NUMEROS ENTRAN 
            canInpares++
        }
    }
    return canInpares // SIEMPRE SE RETORNO FUERA DEL FOR 
}
