const edades = [10, 9, 8, 22, 55, 80, 1, 63, 29, 9]
const menor = (arrayEdades) => { //FUNCION ARROW O FLECHA
    let menor = arrayEdades[0]
    for (let i = 0; i < arrayEdades.length; i++) {
        //console.log(arrayEdades[i])
        if (menor > arrayEdades[i]) {
            menor = arrayEdades[i]

        }
    }
    return menor
}
console.log(menor(edades))